import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-reminder-form',
  templateUrl: './reminder-form.component.html',
  styleUrls: ['./reminder-form.component.css']
})
export class ReminderFormComponent {
 reminderForm: FormGroup;
  successMessage = '';
  errorMessage = '';

  constructor(private fb: FormBuilder, private http: HttpClient) {
    this.reminderForm = this.fb.group({
      date: ['', Validators.required],
      time: ['', Validators.required],
      message: ['', Validators.required],
      method: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.reminderForm.valid) {
      this.http.post('http://localhost:3000/api/reminders', this.reminderForm.value)
        .subscribe({
          next: () => {
            this.successMessage = 'Reminder saved successfully!';
            this.errorMessage = '';
            this.reminderForm.reset();
          },
          error: (err) => {
            this.errorMessage = 'Failed to save reminder. Try again.';
            this.successMessage = '';
          }
        });
    }
  }
}
<div class="container">
  <div class="text">
    Set a Reminder
  </div>

  <form [formGroup]="reminderForm" (ngSubmit)="onSubmit()">
    <!-- Date and Time Row -->
    <div class="form-row">
      <div class="input-data">
        <input type="date" formControlName="date" required />
        <div class="underline"></div>
        <label>Date</label>
      </div>
      <div class="input-data">
        <input type="time" formControlName="time" required />
        <div class="underline"></div>
        <label>Time</label>
      </div>
    </div>

    <!-- Message -->
    <div class="form-row">
      <div class="input-data textarea">
        <textarea formControlName="message" rows="6" required></textarea>
        <div class="underline"></div>
        <label>Write your message</label>
      </div>
    </div>

    <!-- Method Dropdown -->
    <div class="form-row">
      <div class="input-data">
        <select formControlName="method" required>
          <option value="">-- Select Method --</option>
          <option value="email">Email</option>
          <option value="sms">SMS</option>
        </select>
        <div class="underline"></div>
        <label>Reminder Method</label>
      </div>
    </div>

    <!-- Submit Button -->
    <div class="form-row submit-btn">
      <div class="input-data">
        <div class="inner"></div>
        <input type="submit" value="Submit" [disabled]="reminderForm.invalid" />
      </div>
    </div>
  </form>

  <p class="success" *ngIf="successMessage">{{ successMessage }}</p>
  <p class="error" *ngIf="errorMessage">{{ errorMessage }}</p>
</div>
